package app.humanize.service.formatters;

import app.humanize.service.relatorios.ReportData;
import com.itextpdf.html2pdf.HtmlConverter;
import com.itextpdf.io.exceptions.IOException;

import java.io.ByteArrayOutputStream;
import java.nio.charset.StandardCharsets;
import java.time.LocalDate;
import java.time.format.DateTimeFormatter;
import java.util.List;

public class PdfFormatter implements IReportFormatter {
    @Override
    public byte[] formatar(ReportData data) {
        String html = construirHtml(data);
        ByteArrayOutputStream out = new ByteArrayOutputStream();

        try {
            HtmlConverter.convertToPdf(html, out);
        } catch (IOException e) {
            e.printStackTrace();
            throw new RuntimeException("Erro ao converter HTML para PDF",e);
        }

        return out.toByteArray();
    }

    /**
     * MÃ©todo auxiliar para criar uma tabela HTML a partir dos ReportData.
     */
    private String construirHtml(ReportData data) {
        StringBuilder sb = new StringBuilder();
        String logoBase64 = "data:image/png;base64,";

        sb.append("<!DOCTYPE html><html><head><meta charset=\"UTF-8\"><title>RelatÃ³rio</title><style>");
        // --- INÃCIO DO CSS ---
        sb.append("body { font-family: -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, Helvetica, Arial, sans-serif; font-size: 10pt; color: #333; line-height: 1.5; margin: 0; padding: 0; }");
        sb.append(".container { padding: 30px; }");
        sb.append(".header { display: flex; align-items: center; padding-bottom: 20px; border-bottom: 1px solid #eee; margin-bottom: 30px; }");
        sb.append(".logo img { max-height: 50px; width: auto; }");
        sb.append(".company-info { margin-left: 20px; font-size: 9pt; color: #555; }");
        sb.append("h1 { color: #333; text-align: center; font-size: 18pt; margin-bottom: 25px; font-weight: 300; }");
        sb.append("table { width: 100%; border-collapse: collapse; margin-top: 15px; }");
        sb.append("th, td { border: 1px solid #ddd; padding: 10px 12px; text-align: left; vertical-align: top; }");
        sb.append("thead th { background-color: #505050; color: white; font-weight: bold; font-size: 10pt; text-transform: uppercase; }");
        sb.append("tbody td { font-size: 9.5pt; }");
        sb.append("tbody tr:nth-child(even) { background-color: #f9f9f9; }");
        sb.append(".footer { margin-top: 30px; padding-top: 15px; border-top: 1px solid #eee; text-align: center; font-size: 8pt; color: #777; }");
        // --- FIM DO CSS ---
        sb.append("</style></head><body><div class=\"container\">");

        sb.append("<div class=\"header\">");
        sb.append("<div class=\"logo\"><img src=\"").append(logoBase64).append("\" alt=\"Logo Humanize\"></div>");
        sb.append("<div class=\"company-info\">Humanize GestÃ£o de RH<br>BrasÃ­lia - DF<br>www.humanize.com.br</div>");
        sb.append("</div>");

        sb.append("<h1>").append(data.getTitle()).append("</h1>");

        sb.append("<table><thead><tr>");
        for (String header : data.getHeaders()) {
            sb.append("<th>").append(header).append("</th>");
        }
        sb.append("</tr></thead><tbody>");
        for (List<String> row : data.getRows()) {
            sb.append("<tr>");
            for (String cell : row) {
                String safeCell = cell != null ? cell.replace("&", "&amp;").replace("<", "&lt;").replace(">", "&gt;") : "";
                sb.append("<td>").append(safeCell).append("</td>");
            }
            sb.append("</tr>");
        }
        sb.append("</tbody></table>");

        sb.append("<div class=\"footer\">");
        sb.append("Gerado em: ").append(LocalDate.now().format(DateTimeFormatter.ofPattern("dd/MM/yyyy")));
        sb.append(" | Â© 2025 Humanize. Todos os direitos reservados.");
        sb.append("</div>");

        sb.append("</div></body></html>");
        return sb.toString();
    }

    @Override
    public String getExtensao() {
        return ".pdf";
    }

    @Override
    public String getDescricaoFiltro() {
        return "Arquivo PDF (*.pdf)";
    }
}
